import React, { Component } from 'react';

class NewRecords extends Component {
    state = {
      records : [],
      formInputs: {
        artist: '',
        title: '',
        label: '',
        year: ''
      }
    }
  handleChange = (event) => {
    const updateInput = Object.assign( this.state.formInputs, { [event.target.id]: event.target.value })
    this.setState(updateInput)
  }
  handleSubmit  = (event) =>{
    event.preventDefault()
    console.log(this.state.formInputs)
  }

  render () {
    return (
      <div className="col-sm-3">
        <nav>
          <h1> New Records?</h1>
            <form onSubmit={this.handleSubmit}>
              <label htmlFor="artist" >Artist</label>
              <input type="text" id="artist" value={this.state.formInputs.artist}
    onChange={this.handleChange} />
              <label htmlFor="title">Title</label>
              <input type="text" id="title" value={this.state.formInputs.title}
    onChange={this.handleChange} />
              <label htmlFor="label">Label</label>
              <input type="text" id="label" value={this.state.formInputs.label}
    onChange={this.handleChange} />
              <label htmlFor="format">Label</label>
              <input type="text" id="format" value={this.state.formInputs.format}
    onChange={this.handleChange} />
              <label htmlFor="year">Released when?</label>
              <input type="integer" id="year" value={this.state.formInputs.year}
    onChange={this.handleChange} />
              <input type="submit" className="submit" />
            </form>
          </nav>
      </div>
    )
  }
}

export default NewRecords;
